mapper.select:
select 
from sys_user

  deleted=1

    AND username LIKE concat('%',#{username},'%')


    AND nick_name LIKE concat('%',#{nickName},'%')


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    AND id=#{userId}


    AND `status`=#{status}

select from sys_user where dept_id in
#{item}
and deleted=1
